public class NestedIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         
		
		int num = 0;
		
		if(num>0) {
			System.out.print("Positive number");
	}   else if (num<0) {
		    System.out.print("Negative number");
	}   else {
		    System.out.print("zero");
	}
	}

}
